typedef struct {
  MSF_UINT32     sort_idx;
  char          *url;
  char          *title;
  int            file_idx;
} bam_hist_history_item_t;


/************************************************************
 * Local definitions
 ************************************************************/

#define BAM_MAX_HISTORY_ITEMS             BAM_CFG_HISTORY_LIST_SIZE


#define BAM_HISTORY_MAX_FILE_SIZE    BAM_CFG_HISTORY_LIST_MAX_MEM
#define BAM_HISTORY_INITIAL_SIZE     BAM_CFG_HISTORY_INITIAL_SIZE
#define BAM_HISTORY_DEFRAG_LIMIT     BAM_CFG_HISTORY_LIST_MAX_MEM
#define BAM_HISTORY_SIZE_INCREMENT   BAM_CFG_HISTORY_LIST_MAX_MEM 


/************************************************************
 * Global Variables
 ************************************************************/
static bam_hist_history_item_t bam_hist_static_history_list[BAM_MAX_HISTORY_ITEMS];
static bam_hist_history_item_t *bam_hist_history_list[BAM_MAX_HISTORY_ITEMS];
static int                    bam_nbr_historyitems = 0;
static void                  *bam_hist_history_file;

/************************************************************
 * Function definitions
 ************************************************************/
static int  bam_hist_remove_history_item(const char*url);
static int  bam_hist_remove_history_item_idx(int idx);
static void bam_hist_init_file_response (int idx, int eof, void* block, int size);

/**************************FILE HANDELING*********************/

static void
bam_hist_create_file_block (bam_hist_history_item_t *p, void **block, int *size)
{
  unsigned char *t;
  int url_len;
  int title_len;


  url_len = strlen (p->url);
  if (p->title == NULL)
  {
    title_len = 0;
  }
  else
  {
    title_len = strlen(p->title);
  }

  *size = 4 + url_len + title_len + 2;

  t = BAM_ALLOC (*size);

  t[0] = (unsigned char)(((p->sort_idx) & 0xFF000000) >> 24);
  t[1] = (unsigned char)(((p->sort_idx) & 0x00FF0000) >> 16);
  t[2] = (unsigned char)(((p->sort_idx) & 0x0000FF00) >> 8);
  t[3] = (unsigned char)(((p->sort_idx) & 0x000000FF));

  strcpy ((char *)&t[4], p->url);
  if (p->title == NULL)
  {
    t[5 + url_len] = '\0';
  }
  else
  {
    strcpy ((char *)&t[5 + url_len], p->title);
  }
  *block = (void*)t;
}

static int
bam_hist_parse_file_block (void *block, int size, bam_hist_history_item_t *p)
{
  MSF_UINT32 sort_idx;
  unsigned char *t;
  int url_len;

  if ((block == NULL) || (size < 5))
    return -1;

  t = (unsigned char*)block;

  sort_idx  = (((MSF_UINT32)t[0]) << 24);
  sort_idx += (((MSF_UINT32)t[1]) << 16);
  sort_idx += (((MSF_UINT32)t[2]) << 8);
  sort_idx += (((MSF_UINT32)t[3]));

  p->sort_idx = sort_idx;

  url_len = strlen((char*)&t[4]);
  if (size < (5 + url_len))
    return -1;

  p->url = msf_cmmn_strdup (MSF_MODID_BAM, (char*)&t[4]);
  p->title = msf_cmmn_strdup (MSF_MODID_BAM, (char*)&t[5 + url_len]);

  return 1;
}

static void bam_hist_init_file_response (int idx, int eof, void* block, int size)
{
  bam_hist_history_item_t *p;
  int i;
  int n;

  if (eof == 0 && bam_nbr_historyitems < BAM_MAX_HISTORY_ITEMS)
  {
    if (bam_hist_parse_file_block (block, size, bam_hist_history_list[bam_nbr_historyitems]) < 0)
      return;

    bam_hist_history_list[bam_nbr_historyitems++]->file_idx = idx;
  }
  else
  {
    /*Sort*/
    for (n = bam_nbr_historyitems - 1; n > 0; n--)
    {
      for (i = 0; i < n; i++)
      {
        if (bam_hist_history_list[i+1]->sort_idx < bam_hist_history_list[i]->sort_idx)
        {
          /*Swap*/
          p = bam_hist_history_list[i];
          bam_hist_history_list[i]   = bam_hist_history_list[i+1];
          bam_hist_history_list[i+1] = p;
        }
      }
    }    
    /*Finished*/    
    bam_main_remove_module_state(BAM_READING_RECENT_PAGE_LIST);
    bam_init_done ();
  }
}


/************************END FILE HANDELING*******************/




/*
 * This function initializes the bookmarks manager and reads the 
 * saved bookmarks from file.
 *
 * PARAMETERS
 *
 * RETURN    : void
 */
void 
bam_hist_init (void)
{
  int i;

  bam_main_set_module_state (BAM_READING_RECENT_PAGE_LIST);

  bam_nbr_historyitems = 0;

  for(i = 0; i < BAM_MAX_HISTORY_ITEMS; i++)
  {
    //bam_hist_history_list[i] = BAM_ALLOCTYPE (bam_hist_history_item_t);
    bam_hist_history_list[i] = &bam_hist_static_history_list[i];
    bam_hist_history_list[i]->sort_idx  = 0;
    bam_hist_history_list[i]->file_idx  = -1;
    bam_hist_history_list[i]->url       = NULL;
    bam_hist_history_list[i]->title     = NULL;
  }
  
  bam_file_init (BAM_CFG_HISTORY_LIST_FILE_PATH, 
                 &bam_hist_history_file, 
                 BAM_HISTORY_MAX_FILE_SIZE, 
                 BAM_HISTORY_INITIAL_SIZE, 
                 BAM_HISTORY_DEFRAG_LIMIT, 
                 BAM_HISTORY_SIZE_INCREMENT,
                 bam_hist_init_file_response);
}

/*
 * This function terminates the bookmark manager and releases all 
 * resources allocated
 *
 * PARAMETERS
 *
 * RETURN    : void
 */
void 
bam_hist_terminate (void)
{
  int i;

  if(bam_hist_history_file != NULL)
  {
    bam_file_close (bam_hist_history_file);
    bam_hist_history_file = NULL;

    for(i = 0; i < BAM_MAX_HISTORY_ITEMS; i++)
    {
      if (bam_hist_history_list[i]->url != NULL)
        BAM_FREE (bam_hist_history_list[i]->url);
      if (bam_hist_history_list[i]->title != NULL)
        BAM_FREE (bam_hist_history_list[i]->title);
      //BAM_FREE (bam_hist_history_list[i]);
    }

    bam_nbr_historyitems = 0;
  }
}


/*
 * Adds an history item to the list.
 *
 * PARAMETERS
 *
 * RETURN    : void
 */
void
bam_hist_add_item (const char *url, const char *title)
{
  bam_hist_history_item_t *p;
  unsigned char *buffer;
  int   buf_len;
  char *host;

  if (url == NULL)
    return;
  host=msf_url_get_host(MSF_MODID_BAM, url);
  if (host == NULL)
    return;

  /*Remove if an old version with the same URL exists,
    if no such item exist remove last item*/
  if (bam_hist_remove_history_item(url) == FALSE)
    if (bam_nbr_historyitems == BAM_MAX_HISTORY_ITEMS){
     bam_hist_remove_history_item_idx (0);
    }

  p = bam_hist_history_list[bam_nbr_historyitems];
  p->url = BAM_ALLOC(strlen(url) + 1);
  strcpy (p->url, url);
  if (title != NULL)
  {
    p->title = BAM_ALLOC(strlen(title) + 1);
    strcpy (p->title, title);
  }
  else
  {
    p->title = NULL;
  }
  p->sort_idx = MSF_TIME_GET_CURRENT();

  bam_hist_create_file_block(p, (void**)&buffer, &buf_len);

  p->file_idx = bam_file_write_block (bam_hist_history_file, buffer, buf_len, NULL);
  BAM_FREE (buffer);

  bam_nbr_historyitems++;
}

