/*
 * Goes through the list and removes items with the
 * same url.
 *
 * PARAMETERS
 *
 * RETURN    True if removed else False: 
 */
static int
bam_hist_remove_history_item(const char*url)
{
  bam_hist_history_item_t *p;
  int i;
  int j;

  for (i=0; i<bam_nbr_historyitems; i++)
  {
    p = bam_hist_history_list[i];
    if(p->url)/*kevin*/
    {
    if (strcmp(p->url, url) == 0 )
      goto found;
    }
  }

  return FALSE;

found:
  /*An item has been found and should be removed*/
  if (p->title)
    BAM_FREE(p->title);
  if (p->url)
    BAM_FREE(p->url);

  p->url   = NULL;
  p->title = NULL;

  bam_file_delete_block (bam_hist_history_file, p->file_idx , NULL);

  if (i < (bam_nbr_historyitems-1) )
  {
    for (j=i; j < (bam_nbr_historyitems - 1); j++)
    {
      p = bam_hist_history_list[j];
      bam_hist_history_list[j] = bam_hist_history_list[j+1];
      bam_hist_history_list[j+1] = p;
    }
  }
  
  bam_nbr_historyitems--;

  return TRUE;
}

static int
bam_hist_remove_history_item_idx(int idx)
{
  bam_hist_history_item_t *p;
  int j;

  if ((idx < 0) || (idx >= bam_nbr_historyitems))
    return FALSE;

  p = bam_hist_history_list[idx];

  if (p->title)
    BAM_FREE(p->title);
  if (p->url)
    BAM_FREE(p->url);

  p->url   = NULL;
  p->title = NULL;

  bam_file_delete_block (bam_hist_history_file, p->file_idx , NULL);

  if (idx < (bam_nbr_historyitems-1) )
  {
    for (j=idx; j < (bam_nbr_historyitems - 1); j++)
    {
      p = bam_hist_history_list[j];
      bam_hist_history_list[j] = bam_hist_history_list[j+1];
      bam_hist_history_list[j+1] = p;
    }
  }
  
  bam_nbr_historyitems--;

  return TRUE;
}


