void bam_hist_ui_handle_get_recent_page_req(MSF_UINT32 index)
{
    /*----------------------------------------------------------------*/
    /* Local Variables                                                */
    /*----------------------------------------------------------------*/
    int ref_index;

    /*----------------------------------------------------------------*/
    /* Code Body                                                      */
    /*----------------------------------------------------------------*/
    ref_index = bam_nbr_historyitems - index - 1;
    if (bam_nbr_historyitems == 0 || (int) ref_index >= bam_nbr_historyitems)
    {
        bam_msg_recent_page_cnf(BAM_HIST_ERROR_NOT_FOUND, NULL);
    }
    else
    {
            bam_hist_ui_item_struct hist_ui_item;

            hist_ui_item.title = bam_hist_history_list[ref_index]->title;
            hist_ui_item.url = bam_hist_history_list[ref_index]->url;

            bam_msg_recent_page_cnf(BAM_HIST_ERROR_OK, &hist_ui_item);
    }
}

